mapper.select:
select
  id, account, password, email, sex, money, status, id_card, id_card_cipher_id, id_card_cipher_text, is_deleted, create_by, create_time, update_by, update_time
from cms_user


        and id = #{id}


        and account = #{account}


        and password = #{password}


        and email = #{email}


        and sex = #{sex}


        and money = #{money}


        and status = #{status}


        and id_card = #{idCard}


        and id_card_cipher_id = #{idCardCipherId}


        and id_card_cipher_text = #{idCardCipherText}


        and is_deleted = #{isDeleted}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from cms_user


        and id = #{id}


        and account = #{account}


        and password = #{password}


        and email = #{email}


        and sex = #{sex}


        and money = #{money}


        and status = #{status}


        and id_card = #{idCard}


        and id_card_cipher_id = #{idCardCipherId}


        and id_card_cipher_text = #{idCardCipherText}


        and is_deleted = #{isDeleted}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}
